import React from 'react';
import { Container, Typography, Box, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from '@mui/material';

type TableRowType = string[];

function HedefNisbetPage({ rows }: { rows: TableRowType[] }) {
  // Verileri kişilere ve günlere göre gruplama
  const idAdList: { id: string; ad: string }[] = [];
  const idSet = new Set<string>();
  const gunlereGoreNisbet: Record<string, string[]> = {};
  let gunSayisi = 0;
  const tarihler: string[] = [];

  // Verileri personel ID ve tarihe göre grupla ve günleri belirle
  rows.forEach((row) => {
    const personelId = row[0]; // Firebase ID
    const personelAdSoyad = row[3]; // AD SOYAD
    const hedefNisbeti = row[7]; // HEDEF NİSBETİ
    const tarih = row[8]; // Tarih (App.tsx'teki 8. sütun tarih olarak eşleştirilmişti)

    if (personelId && !idSet.has(personelId)) {
      idAdList.push({ id: personelId, ad: personelAdSoyad });
      idSet.add(personelId);
      gunlereGoreNisbet[personelId] = [];
    }

    if (tarih && !tarihler.includes(tarih)) {
      tarihler.push(tarih);
    }
  });

  tarihler.sort(); // Tarihleri sırala
  gunSayisi = tarihler.length;

  // Her personelin her gününe ait hedef nisbeti değerlerini yerleştir
  rows.forEach(row => {
    const personelId = row[0];
    const hedefNisbeti = row[7];
    const tarih = row[8];
    const gunIndex = tarihler.indexOf(tarih);

    if (personelId && gunIndex !== -1) {
       gunlereGoreNisbet[personelId][gunIndex] = hedefNisbeti || '';
    }
  });

  // Tablo başlıklarını oluştur: AD SOYAD ve günler
  const tableHeaders = [
    'AD SOYAD',
    ...tarihler.map((tarih, index) => `${index + 1}. Gün (${tarih})`)
  ];

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Box sx={{ p: { xs: 1, sm: 3 } }}>
        <Typography variant="h4" gutterBottom sx={{ fontSize: { xs: 20, sm: 24, md: 32 }, mb: { xs: 2, sm: 3 } }}>
          Günlük Hedef Nisbeti
        </Typography>
        <TableContainer component={Paper} sx={{ overflowX: 'auto' }}>
          <Table>
            <TableHead>
              <TableRow>
                {tableHeaders.map((header, index) => (
                  <TableCell key={index}>{header}</TableCell>
                ))}
              </TableRow>
            </TableHead>
            <TableBody>
              {idAdList.map(({ id, ad }) => (
                <TableRow key={id}>
                  <TableCell>{ad}</TableCell>
                  {/* Her gün için hedef nisbeti değerlerini göster */}
                  {Array.from({ length: gunSayisi }).map((_, gunIndex) => (
                    <TableCell key={gunIndex}>{gunlereGoreNisbet[id][gunIndex] || ''}</TableCell>
                  ))}
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Box>
    </Container>
  );
}

export default HedefNisbetPage;
