import React, { useState } from 'react';
import { Container, Typography, Box, Card, CardContent, Button, Dialog, DialogTitle, DialogContent, DialogActions, List, ListItem, ListItemIcon, ListItemText, Grid, TableContainer, Table, TableHead, TableBody, TableRow, TableCell, Paper } from '@mui/material';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';
import { Cell, Legend, Pie, PieChart, ResponsiveContainer, Tooltip, BarChart, CartesianGrid, XAxis, YAxis, Bar } from 'recharts';

type TableRowType = string[];

function DashboardPage({ rows }: { rows: TableRowType[] }) {
    const [openDetay, setOpenDetay] = useState(false);
    // 1. Tüm benzersiz ID ve ad-soyadları bul
    const idAdList: { id: string, ad: string }[] = [];
    const idSet = new Set<string>();
    rows.forEach(row => {
      if (row[0] && !idSet.has(row[0])) {
        idAdList.push({ id: row[0], ad: row[3] });
        idSet.add(row[0]);
      }
    });
    const kisiSayisi = idAdList.length;
    const toplamGun = kisiSayisi > 0 ? Math.floor(rows.length / kisiSayisi) : 0;
  
    // Son gün ve genel ortalamalar
    function parsePercent(val: string | undefined) {
      if (!val) return 0;
      return parseFloat(val.replace('%', '').replace(',', '.')) || 0;
    }
    // Son günün tüm HEDEF NİSBETİ değerleri
    const idyeGoreNisbet: Record<string, string[]> = {};
    idAdList.forEach(({ id }) => { idyeGoreNisbet[id] = []; });
    for (let gun = 0; gun < toplamGun; gun++) {
      for (let kisi = 0; kisi < kisiSayisi; kisi++) {
        const rowIdx = gun * kisiSayisi + kisi;
        const row = rows[rowIdx];
        if (row && row[0] && idyeGoreNisbet[row[0]]) {
          idyeGoreNisbet[row[0]][gun] = row[8];
        }
      }
    }
    const sonGunNisbetleri = idAdList.map(({ id }) => {
      const sonGun = toplamGun > 0 ? idyeGoreNisbet[id][toplamGun - 1] : '';
      return parsePercent(sonGun);
    });
    const ortalama = sonGunNisbetleri.length > 0
      ? sonGunNisbetleri.reduce((a, b) => a + b, 0) / sonGunNisbetleri.length
      : 0;
    // Son günün lideri
    let liderAd = '';
    let liderArtis = -Infinity;
    idAdList.forEach(({ id, ad }) => {
      const sonGun = toplamGun > 0 ? idyeGoreNisbet[id][toplamGun - 1] : '';
      const oncekiGun = toplamGun > 1 ? idyeGoreNisbet[id][toplamGun - 2] : '';
      const artis = parsePercent(sonGun) - parsePercent(oncekiGun);
      if (artis > liderArtis) {
        liderArtis = artis;
        liderAd = ad;
      }
    });
    // Pie chart için üstünde ve altında kalanlar
    const gunlukArtisliListe = idAdList.map(({ id }) => {
      const sonGun = toplamGun > 0 ? idyeGoreNisbet[id][toplamGun - 1] : '';
      const oncekiGun = toplamGun > 1 ? idyeGoreNisbet[id][toplamGun - 2] : '';
      const artis = parsePercent(sonGun) - parsePercent(oncekiGun);
      return artis;
    });
    const gunlukArtisOrtalama = gunlukArtisliListe.length > 0 ? gunlukArtisliListe.reduce((a, b) => a + b, 0) / gunlukArtisliListe.length : 0;
    const ustunde = gunlukArtisliListe.filter(a => a > gunlukArtisOrtalama).length;
    const altinda = gunlukArtisliListe.filter(a => a < gunlukArtisOrtalama).length;
    const esit = gunlukArtisliListe.length - ustunde - altinda;
    const pieData = [
      { name: 'Üstünde', value: ustunde },
      { name: 'Altında', value: altinda },
      { name: 'Eşit', value: esit },
    ];
    const pieColors = ['#43a047', '#e53935', '#fbc02d'];
    // Kalan gün hesabı (7 Haziran 2025 Cumartesi)
    const hedefTarih = new Date('2025-06-07');
    const bugun = new Date();
    const kalanGun = Math.ceil((hedefTarih.getTime() - bugun.getTime()) / (1000 * 60 * 60 * 24));
    console.log(hedefTarih.getTime());
    console.log(bugun.getTime());
    console.log(kalanGun);
  
    // Tüm personelin son HEDEF NİSBETİ değerlerini bul
    const genelNisbetler: { id: string, ad: string, nisbet: number }[] = idAdList.map(({ id, ad }) => {
      // Son günün HEDEF NİSBETİ
      const sonNisbet = toplamGun > 0 ? idyeGoreNisbet[id][toplamGun - 1] : '';
      const val = parseFloat((sonNisbet || '').replace('%', '').replace(',', '.'));
      return { id, ad, nisbet: isNaN(val) ? 0 : val };
    });
    const genelOrtalama = genelNisbetler.length > 0
      ? genelNisbetler.reduce((a, b) => a + b.nisbet, 0) / genelNisbetler.length
      : 0;
    const genelUstunde = genelNisbetler.filter(p => p.nisbet > genelOrtalama);
    const genelAltinda = genelNisbetler.filter(p => p.nisbet < genelOrtalama);
  
    const chartData = genelNisbetler.map(({ ad, nisbet }) => ({
      name: ad,
      ortalama: nisbet
    }));
  
    return (
      <Container maxWidth="lg" sx={{ mt: 4 }}>
        <Box sx={{ p: { xs: 1, sm: 3 } }}>
          <Typography variant="h4" gutterBottom sx={{ fontSize: { xs: 20, sm: 24, md: 32 }, mb: { xs: 2, sm: 3 } }}>
            Dashboard
          </Typography>
          <Grid container spacing={{ xs: 2, sm: 3 }}>
            <Grid xs={12} md={6}>
              <Card sx={{ height: '100%', minHeight: { xs: 300, sm: 400 } }}>
                <CardContent>
                  <Typography variant="h6" sx={{ fontSize: { xs: 16, sm: 20 }, mb: 2 }}>
                    Genel Ortalama Grafiği
                  </Typography>
                  <ResponsiveContainer width="100%" height={300}>
                    <BarChart data={chartData}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="name" tick={{ fontSize: 12 }} />
                      <YAxis tick={{ fontSize: 12 }} />
                      <Tooltip />
                      <Legend />
                      <Bar dataKey="ortalama" fill="#8884d8" />
                    </BarChart>
                  </ResponsiveContainer>
                </CardContent>
              </Card>
            </Grid>
            <Grid xs={12} md={6}>
              <Card sx={{ height: '100%' }}>
                <CardContent>
                  <Typography variant="h6" sx={{ fontSize: { xs: 16, sm: 20 }, mb: 2 }}>
                    Detaylı Liste
                  </Typography>
                  <TableContainer component={Paper} sx={{ overflowX: 'auto' }}>
                    <Table size="small">
                      <TableHead>
                        <TableRow>
                          <TableCell sx={{ fontSize: { xs: 12, sm: 14 } }}>İsim</TableCell>
                          <TableCell sx={{ fontSize: { xs: 12, sm: 14 } }}>Ortalama</TableCell>
                        </TableRow>
                      </TableHead>
                      <TableBody>
                        {chartData.map((row, index) => (
                          <TableRow key={index}>
                            <TableCell sx={{ fontSize: { xs: 12, sm: 14 } }}>{row.name}</TableCell>
                            <TableCell sx={{ fontSize: { xs: 12, sm: 14 } }}>{row.ortalama}%</TableCell>
                          </TableRow>
                        ))}
                      </TableBody>
                    </Table>
                  </TableContainer>
                </CardContent>
              </Card>
            </Grid>
          </Grid>
        </Box>
        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 3, mb: 4, justifyContent: 'center' }}>
          <Box sx={{ minWidth: 220, p: 3, bgcolor: '#e3f2fd', borderRadius: 2, textAlign: 'center', boxShadow: 1 }}>
            <Typography variant="subtitle2" color="text.secondary">Toplam Personel</Typography>
            <Typography variant="h5" color="primary" fontWeight={700}>{kisiSayisi}</Typography>
          </Box>
          <Box sx={{ minWidth: 220, p: 3, bgcolor: '#f5f5f5', borderRadius: 2, textAlign: 'center', boxShadow: 1 }}>
            <Typography variant="subtitle2" color="text.secondary">Toplam Gün</Typography>
            <Typography variant="h5" color="primary" fontWeight={700}>{toplamGun}</Typography>
          </Box>
          <Box sx={{ minWidth: 220, p: 3, bgcolor: '#f5f5f5', borderRadius: 2, textAlign: 'center', boxShadow: 1 }}>
            <Typography variant="subtitle2" color="text.secondary">Son Gün HEDEF NİSBETİ Ortalaması</Typography>
            <Typography variant="h5" color="primary" fontWeight={700}>%{ortalama.toFixed(1)}</Typography>
          </Box>
          <Box sx={{ minWidth: 220, p: 3, bgcolor: '#f5f5f5', borderRadius: 2, textAlign: 'center', boxShadow: 1 }}>
            <Typography variant="subtitle2" color="text.secondary">Günlük Artış Ortalaması</Typography>
            <Typography variant="h5" color="primary" fontWeight={700}>%{gunlukArtisOrtalama.toFixed(1)}</Typography>
          </Box>
          <Box sx={{ minWidth: 220, p: 3, bgcolor: '#f5f5f5', borderRadius: 2, textAlign: 'center', boxShadow: 1 }}>
            <Typography variant="subtitle2" color="text.secondary">Son Kaç Gün Kaldı</Typography>
            <Typography variant="h5" color="primary" fontWeight={700}>{kalanGun}</Typography>
          </Box>
          <Box sx={{ minWidth: 320, p: 3, bgcolor: '#f5f5f5', borderRadius: 2, textAlign: 'center', boxShadow: 1 }}>
            <Typography variant="subtitle2" color="text.secondary" sx={{ mb: 1 }}>
              Genel HEDEF NİSBETİ Ortalaması
            </Typography>
            <Typography variant="h5" color="primary" fontWeight={700}>%{genelOrtalama.toFixed(1)}</Typography>
            <Typography variant="body2" color="success.main">Üstünde: {genelUstunde.length} kişi</Typography>
            <Typography variant="body2" color="error.main">Altında: {genelAltinda.length} kişi</Typography>
            <Button size="small" variant="outlined" sx={{ mt: 1 }} onClick={() => setOpenDetay(true)}>Detay</Button>
          </Box>
        </Box>
        <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 3, mb: 4, justifyContent: 'center', alignItems: 'center' }}>
          <Box sx={{ minWidth: 320, p: 3, bgcolor: '#e8f5e9', borderRadius: 2, textAlign: 'center', boxShadow: 1 }}>
            <Typography variant="subtitle2" color="text.secondary">Son Günün Lideri</Typography>
            <Typography variant="h6" color="primary" fontWeight={700}>{liderAd || '-'}</Typography>
            <Typography variant="body2" color="text.secondary">Artış: {liderArtis > 0 ? `%${liderArtis.toFixed(1)}` : '%0.0'}</Typography>
          </Box>
          <Box sx={{ minWidth: 320, height: 220, p: 3, bgcolor: '#f5f5f5', borderRadius: 2, textAlign: 'center', boxShadow: 1, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
            <Typography variant="subtitle2" color="text.secondary" sx={{ mb: 1 }}>Günlük Artış Dağılımı</Typography>
            <ResponsiveContainer width="100%" height={120}>
              <PieChart>
                <Pie data={pieData} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={40} label>
                  {pieData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={pieColors[index % pieColors.length]} />
                  ))}
                </Pie>
                <Tooltip />
                <Legend />
              </PieChart>
            </ResponsiveContainer>
          </Box>
          <Box sx={{ minWidth: 320, p: 3, bgcolor: '#f5f5f5', borderRadius: 2, textAlign: 'center', boxShadow: 1, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
            <Typography variant="subtitle2" color="text.secondary" sx={{ mb: 1 }}>Ortalama Karşılaştırması</Typography>
            <Typography variant="body1" color="success.main" fontWeight={700}>Üstünde: {ustunde} kişi</Typography>
            <Typography variant="body1" color="error.main" fontWeight={700}>Altında: {altinda} kişi</Typography>
          </Box>
        </Box>
        <Box sx={{ textAlign: 'center', mt: 4, color: '#888' }}>
          <Typography variant="h6" fontWeight={500}>
            Başarı, küçük adımların toplamıdır. Her gün bir adım daha ileri!
          </Typography>
        </Box>
        <Dialog
          open={openDetay}
          onClose={() => setOpenDetay(false)}
          maxWidth="md"
          fullWidth
          PaperProps={{
            sx: {
              width: { xs: '95%', sm: '80%', md: '60%' },
              maxHeight: { xs: '90vh', sm: '80vh' }
            }
          }}
        >
          <DialogTitle sx={{ fontSize: { xs: 18, sm: 24 } }}>Detaylı Bilgi</DialogTitle>
          <DialogContent dividers>
            <Grid container spacing={2}>
              <Grid xs={12} md={6}>
                <Card sx={{ mb: 2 }}>
                  <CardContent>
                    <Typography variant="h6" sx={{ fontSize: { xs: 16, sm: 20 }, mb: 2, color: 'success.main' }}>
                      Ortalamanın Üstündekiler
                    </Typography>
                    <List>
                      {genelUstunde.map((p, index) => (
                        <ListItem key={index}>
                          <ListItemIcon>
                            <ArrowUpwardIcon color="success" />
                          </ListItemIcon>
                          <ListItemText 
                            primary={p.ad} 
                            secondary={`Ortalama: ${p.nisbet}%`}
                            primaryTypographyProps={{ fontSize: { xs: 14, sm: 16 } }}
                            secondaryTypographyProps={{ fontSize: { xs: 12, sm: 14 } }}
                          />
                        </ListItem>
                      ))}
                    </List>
                  </CardContent>
                </Card>
              </Grid>
              <Grid xs={12} md={6}>
                <Card>
                  <CardContent>
                    <Typography variant="h6" sx={{ fontSize: { xs: 16, sm: 20 }, mb: 2, color: 'error.main' }}>
                      Ortalamanın Altındakiler
                    </Typography>
                    <List>
                      {genelAltinda.map((p, index) => (
                        <ListItem key={index}>
                          <ListItemIcon>
                            <ArrowDownwardIcon color="error" />
                          </ListItemIcon>
                          <ListItemText 
                            primary={p.ad} 
                            secondary={`Ortalama: ${p.nisbet}%`}
                            primaryTypographyProps={{ fontSize: { xs: 14, sm: 16 } }}
                            secondaryTypographyProps={{ fontSize: { xs: 12, sm: 14 } }}
                          />
                        </ListItem>
                      ))}
                    </List>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenDetay(false)} sx={{ fontSize: { xs: 12, sm: 14 } }}>Kapat</Button>
          </DialogActions>
        </Dialog>
      </Container>
    );
  }

export default DashboardPage;
