import React, { useState } from 'react';
import { 
  AppBar, 
  Toolbar, 
  Typography, 
  Container, 
  Box, 
  Button, 
  Dialog, 
  DialogTitle, 
  DialogContent, 
  DialogActions, 
  TextField, 
  Table, 
  TableBody, 
  TableCell, 
  TableContainer, 
  TableHead, 
  TableRow, 
  Paper, 
  ThemeProvider,
  createTheme,
  } from '@mui/material';
import { 
  Home as HomeIcon,
  Info as InfoIcon,
  ContactMail as ContactIcon,
  Work as WorkIcon,
  BarChart as BarChartIcon
} from '@mui/icons-material';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { Cell, Legend, Pie, PieChart, ResponsiveContainer ,Tooltip} from 'recharts';
import dayjs from 'dayjs';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

const TABLE_HEADERS = [
  'ID',
  'FAALİYET TİP',
  'Sütun1',
  'AD SOYAD',
  'Sütun2',
  'TİP',
  'TOPLAM',
  'HEDEF',
  'HEDEF NİSBETİ',
];

type TableRowType = string[];

function DataTablePage({ rows, onAddRows }: { rows: TableRowType[]; onAddRows: (newRows: TableRowType[]) => void }) {
  const [dialogOpen, setDialogOpen] = useState(false);
  const [inputValue, setInputValue] = useState('');

  const handleOpenDialog = () => setDialogOpen(true);
  const handleCloseDialog = () => {
    setDialogOpen(false);
    setInputValue('');
  };

  const handleAddRows = () => {
    const newRows = inputValue
      .split('\n')
      .map(line => line.trim())
      .filter(line => line.length > 0)
      .map(line => line.split('\t'));
    onAddRows(newRows);
    handleCloseDialog();
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="h5">Veri Tablosu</Typography>
        <Button variant="contained" color="primary" onClick={handleOpenDialog}>
          Ekle
        </Button>
      </Box>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              {TABLE_HEADERS.map(header => (
                <TableCell key={header} sx={{ background: '#6ba6d7', color: 'white', fontWeight: 'bold' }}>{header}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row, idx) => (
              <TableRow key={idx}>
                {TABLE_HEADERS.map((_, colIdx) => (
                  <TableCell key={colIdx}>{row[colIdx] || ''}</TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      {/* Dialog */}
      <Dialog open={dialogOpen} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
        <DialogTitle>Veri Ekle</DialogTitle>
        <DialogContent>
          <Typography variant="body2" sx={{ mb: 1 }}>
            Lütfen başka bir yerden (ör. Excel) kopyaladığınız verileri buraya yapıştırın. Her satır yeni bir kayıt, sütunlar ise TAB ile ayrılmış olmalı.
          </Typography>
          <TextField
            autoFocus
            multiline
            minRows={6}
            fullWidth
            variant="outlined"
            placeholder={`ID\tFAALİYET TİP\tSütun1\tAD SOYAD\tSütun2\tTİP\tTOPLAM\tHEDEF\tHEDEF NİSBETİ`}
            value={inputValue}
            onChange={e => setInputValue(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>İptal</Button>
          <Button onClick={handleAddRows} variant="contained" color="primary">Ekle</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

function HedefNisbetPage({ rows }: { rows: TableRowType[] }) {
  const today = new Date().toLocaleDateString('tr-TR');

  // 1. Tüm benzersiz ID ve ad-soyadları bul
  const idAdList: { id: string, ad: string }[] = [];
  const idSet = new Set<string>();
  rows.forEach(row => {
    if (row[0] && !idSet.has(row[0])) {
      idAdList.push({ id: row[0], ad: row[3] });
      idSet.add(row[0]);
    }
  });

  // 2. Her gün kaç personel var? (ilk günün ID sayısı kadar)
  const kisiSayisi = idAdList.length;
  const toplamGun = Math.floor(rows.length / kisiSayisi);

  // 3. ID'ye göre her günün HEDEF NİSBETİ değerlerini topla
  // idyeGoreNisbet[id][gun] = yüzdelik
  const idyeGoreNisbet: Record<string, string[]> = {};
  idAdList.forEach(({ id }) => {
    idyeGoreNisbet[id] = [];
  });
  for (let gun = 0; gun < toplamGun; gun++) {
    for (let kisi = 0; kisi < kisiSayisi; kisi++) {
      const rowIdx = gun * kisiSayisi + kisi;
      const row = rows[rowIdx];
      if (row && row[0] && idyeGoreNisbet[row[0]]) {
        idyeGoreNisbet[row[0]][gun] = row[8];
      }
    }
  }

  // 4. Sütun başlıkları: ID, AD SOYAD, 1. Gün, 2. Gün, ...
  const columns = ['ID', 'AD SOYAD', ...Array.from({ length: toplamGun }, (_, i) => `${i + 1}. Gün`)];
  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Typography variant="h5" sx={{ mb: 2 }}>Günlük Hedef Nisbeti ({today})</Typography>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              {columns.map(col => (
                <TableCell key={col} sx={{ background: '#6ba6d7', color: 'white', fontWeight: 'bold' }}>{col}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {idAdList.map(({ id, ad }) => (
              <TableRow key={id}>
                <TableCell>{id}</TableCell>
                <TableCell>{ad}</TableCell>
                {Array.from({ length: toplamGun }).map((_, gunIdx) => (
                  <TableCell key={gunIdx}>{idyeGoreNisbet[id][gunIdx] || ''}</TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
}

function GunlukArtisYuzdesiPage({ rows }: { rows: TableRowType[] }) {
  // 1. Tüm benzersiz ID ve ad-soyadları bul
  const idAdList: { id: string, ad: string }[] = [];
  const idSet = new Set<string>();
  rows.forEach(row => {
    if (row[0] && !idSet.has(row[0])) {
      idAdList.push({ id: row[0], ad: row[3] });
      idSet.add(row[0]);
    }
  });

  // 2. Her gün kaç personel var? (ilk günün ID sayısı kadar)
  const kisiSayisi = idAdList.length;
  const toplamGun = Math.floor(rows.length / kisiSayisi);

  // 3. ID'ye göre her günün HEDEF NİSBETİ değerlerini topla
  const idyeGoreNisbet: Record<string, string[]> = {};
  idAdList.forEach(({ id }) => {
    idyeGoreNisbet[id] = [];
  });
  for (let gun = 0; gun < toplamGun; gun++) {
    for (let kisi = 0; kisi < kisiSayisi; kisi++) {
      const rowIdx = gun * kisiSayisi + kisi;
      const row = rows[rowIdx];
      if (row && row[0] && idyeGoreNisbet[row[0]]) {
        idyeGoreNisbet[row[0]][gun] = row[8];
      }
    }
  }

  // 4. Tablo başlıkları: ID, AD SOYAD, Son Gün, Bir Önceki Gün, Artış (%)
  const columns = ['ID', 'AD SOYAD', 'Son Gün', 'Bir Önceki Gün', 'Artış (%)'];

  // 5. Son iki günün değerlerini ve farkını hesapla
  function parsePercent(val: string | undefined) {
    if (!val) return 0;
    // % işareti varsa kaldır
    return parseFloat(val.replace('%', '').replace(',', '.')) || 0;
  }

  // Son günün tüm HEDEF NİSBETİ değerlerinin ortalamasını hesapla
  const sonGunNisbetleri = idAdList.map(({ id }) => {
    const sonGun = toplamGun > 0 ? idyeGoreNisbet[id][toplamGun - 1] : '';
    return parsePercent(sonGun);
  });
  const ortalama = sonGunNisbetleri.length > 0
    ? sonGunNisbetleri.reduce((a, b) => a + b, 0) / sonGunNisbetleri.length
    : 0;

  // Lideri bul
  let liderAd = '';
  let liderArtis = -Infinity;
  idAdList.forEach(({ id, ad }) => {
    const sonGun = toplamGun > 0 ? idyeGoreNisbet[id][toplamGun - 1] : '';
    const oncekiGun = toplamGun > 1 ? idyeGoreNisbet[id][toplamGun - 2] : '';
    const artis = parsePercent(sonGun) - parsePercent(oncekiGun);
    if (artis > liderArtis) {
      liderArtis = artis;
      liderAd = ad;
    }
  });

  // Personelleri günlük artışa göre sırala
  const artisliListe = idAdList.map(({ id, ad }) => {
    const sonGun = toplamGun > 0 ? idyeGoreNisbet[id][toplamGun - 1] : '';
    const oncekiGun = toplamGun > 1 ? idyeGoreNisbet[id][toplamGun - 2] : '';
    const artis = parsePercent(sonGun) - parsePercent(oncekiGun);
    const sonGunYuzde = parsePercent(sonGun);
    return {
      id,
      ad,
      sonGun,
      oncekiGun,
      artis,
      sonGunYuzde
    };
  });
  artisliListe.sort((a, b) => b.artis - a.artis);

  // Günlük artış ortalamasını hesapla
  const gunlukArtisOrtalama = artisliListe.length > 0
    ? artisliListe.reduce((a, b) => a + b.artis, 0) / artisliListe.length
    : 0;

  // Kalan gün hesabı (7 Haziran 2024 Cumartesi)
  const hedefTarih = dayjs('2024-06-07');
  const bugun = dayjs().startOf('day');
  const kalanGun = hedefTarih.diff(bugun, 'day') + 1;

  // Kutucuklar için bilgiler
  const infoBoxes = [
    {
      label: 'Genel Günlük Artış Ortalaması',
      value: `%${gunlukArtisOrtalama.toFixed(1)}`
    },
    {
      label: 'Hedefe Olan Nisbet Oranı',
      value: `%${ortalama.toFixed(1)}`
    },
    {
      label: 'Son Kaç Gün Kaldı',
      value: kalanGun > 0 ? kalanGun : 0
    }
  ];

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <img
        src="/motivasyon.png"
        alt="Motivasyon"
        style={{
          width: '100%',
          maxWidth: 600,
          display: 'block',
          margin: '24px auto 16px auto',
          borderRadius: 16,
          boxShadow: '0 2px 8px rgba(0,0,0,0.08)'
        }}
      />
      <Typography variant="h5" sx={{ mb: 2 }}>Günlük Artış Yüzdesi</Typography>
      {toplamGun > 1 && liderAd && (
        <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
          <Box sx={{ flex: 2, p: 2, bgcolor: '#e3f2fd', borderRadius: 2, textAlign: 'center' }}>
            <Typography variant="h6" color="primary">
              Günün Lideri: {liderAd} ({liderArtis > 0 ? `%${liderArtis.toFixed(1)}` : '%0.0'})
            </Typography>
          </Box>
          {infoBoxes.map((box, i) => (
            <Box key={i} sx={{ flex: 1, p: 2, bgcolor: '#f5f5f5', borderRadius: 2, textAlign: 'center', border: '1px solid #90caf9' }}>
              <Typography variant="subtitle2" color="text.secondary">{box.label}</Typography>
              <Typography variant="h6" color="primary">{box.value}</Typography>
            </Box>
          ))}
        </Box>
      )}
      <Typography variant="body2" sx={{ mb: 2 }}>
        Son gün HEDEF NİSBETİ ortalaması: <b>%{ortalama.toFixed(1)}</b><br/>
        Günlük artış ortalaması: <b>%{gunlukArtisOrtalama.toFixed(1)}</b>
      </Typography>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              {columns.map(col => (
                <TableCell key={col} sx={{ background: '#6ba6d7', color: 'white', fontWeight: 'bold' }}>{col}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {artisliListe.map(({ id, ad, sonGun, oncekiGun, artis, sonGunYuzde }) => {
              let bgColor = '#fffde7'; // eşitse sarımsı
              if (artis > gunlukArtisOrtalama) bgColor = '#e8f5e9'; // üstünde yeşil
              else if (artis < gunlukArtisOrtalama) bgColor = '#ffebee'; // altında pembe
              return (
                <TableRow
                  key={id}
                  sx={{ backgroundColor: bgColor }}
                >
                  <TableCell>{id}</TableCell>
                  <TableCell>{ad}</TableCell>
                  <TableCell>{sonGun || ''}</TableCell>
                  <TableCell>{oncekiGun || ''}</TableCell>
                  <TableCell>{toplamGun > 1 ? `%${artis.toFixed(1)}` : ''}</TableCell>
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
}

function DashboardPage({ rows }: { rows: TableRowType[] }) {
  // 1. Tüm benzersiz ID ve ad-soyadları bul
  const idAdList: { id: string, ad: string }[] = [];
  const idSet = new Set<string>();
  rows.forEach(row => {
    if (row[0] && !idSet.has(row[0])) {
      idAdList.push({ id: row[0], ad: row[3] });
      idSet.add(row[0]);
    }
  });
  const kisiSayisi = idAdList.length;
  const toplamGun = kisiSayisi > 0 ? Math.floor(rows.length / kisiSayisi) : 0;

  // Son gün ve genel ortalamalar
  function parsePercent(val: string | undefined) {
    if (!val) return 0;
    return parseFloat(val.replace('%', '').replace(',', '.')) || 0;
  }
  // Son günün tüm HEDEF NİSBETİ değerleri
  const idyeGoreNisbet: Record<string, string[]> = {};
  idAdList.forEach(({ id }) => { idyeGoreNisbet[id] = []; });
  for (let gun = 0; gun < toplamGun; gun++) {
    for (let kisi = 0; kisi < kisiSayisi; kisi++) {
      const rowIdx = gun * kisiSayisi + kisi;
      const row = rows[rowIdx];
      if (row && row[0] && idyeGoreNisbet[row[0]]) {
        idyeGoreNisbet[row[0]][gun] = row[8];
      }
    }
  }
  const sonGunNisbetleri = idAdList.map(({ id }) => {
    const sonGun = toplamGun > 0 ? idyeGoreNisbet[id][toplamGun - 1] : '';
    return parsePercent(sonGun);
  });
  const ortalama = sonGunNisbetleri.length > 0
    ? sonGunNisbetleri.reduce((a, b) => a + b, 0) / sonGunNisbetleri.length
    : 0;
  // Son günün lideri
  let liderAd = '';
  let liderArtis = -Infinity;
  idAdList.forEach(({ id, ad }) => {
    const sonGun = toplamGun > 0 ? idyeGoreNisbet[id][toplamGun - 1] : '';
    const oncekiGun = toplamGun > 1 ? idyeGoreNisbet[id][toplamGun - 2] : '';
    const artis = parsePercent(sonGun) - parsePercent(oncekiGun);
    if (artis > liderArtis) {
      liderArtis = artis;
      liderAd = ad;
    }
  });
  // Pie chart için üstünde ve altında kalanlar
  const gunlukArtisliListe = idAdList.map(({ id }) => {
    const sonGun = toplamGun > 0 ? idyeGoreNisbet[id][toplamGun - 1] : '';
    const oncekiGun = toplamGun > 1 ? idyeGoreNisbet[id][toplamGun - 2] : '';
    const artis = parsePercent(sonGun) - parsePercent(oncekiGun);
    return artis;
  });
  const gunlukArtisOrtalama = gunlukArtisliListe.length > 0 ? gunlukArtisliListe.reduce((a, b) => a + b, 0) / gunlukArtisliListe.length : 0;
  const ustunde = gunlukArtisliListe.filter(a => a > gunlukArtisOrtalama).length;
  const altinda = gunlukArtisliListe.filter(a => a < gunlukArtisOrtalama).length;
  const esit = gunlukArtisliListe.length - ustunde - altinda;
  const pieData = [
    { name: 'Üstünde', value: ustunde },
    { name: 'Altında', value: altinda },
    { name: 'Eşit', value: esit },
  ];
  const pieColors = ['#43a047', '#e53935', '#fbc02d'];
  // Kalan gün hesabı (7 Haziran 2024 Cumartesi)
  const hedefTarih = dayjs('2025-06-07');
  const bugun = dayjs().startOf('day');
  const kalanGun = hedefTarih.diff(bugun, 'day') + 1;

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" sx={{ mb: 3, textAlign: 'center', fontWeight: 700 }}>
        Hoşgeldiniz! Dashboard
      </Typography>
      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 3, mb: 4, justifyContent: 'center' }}>
        <Box sx={{ minWidth: 220, p: 3, bgcolor: '#e3f2fd', borderRadius: 2, textAlign: 'center', boxShadow: 1 }}>
          <Typography variant="subtitle2" color="text.secondary">Toplam Personel</Typography>
          <Typography variant="h5" color="primary" fontWeight={700}>{kisiSayisi}</Typography>
        </Box>
        <Box sx={{ minWidth: 220, p: 3, bgcolor: '#f5f5f5', borderRadius: 2, textAlign: 'center', boxShadow: 1 }}>
          <Typography variant="subtitle2" color="text.secondary">Toplam Gün</Typography>
          <Typography variant="h5" color="primary" fontWeight={700}>{toplamGun}</Typography>
        </Box>
        <Box sx={{ minWidth: 220, p: 3, bgcolor: '#f5f5f5', borderRadius: 2, textAlign: 'center', boxShadow: 1 }}>
          <Typography variant="subtitle2" color="text.secondary">Son Gün HEDEF NİSBETİ Ortalaması</Typography>
          <Typography variant="h5" color="primary" fontWeight={700}>%{ortalama.toFixed(1)}</Typography>
        </Box>
        <Box sx={{ minWidth: 220, p: 3, bgcolor: '#f5f5f5', borderRadius: 2, textAlign: 'center', boxShadow: 1 }}>
          <Typography variant="subtitle2" color="text.secondary">Günlük Artış Ortalaması</Typography>
          <Typography variant="h5" color="primary" fontWeight={700}>%{gunlukArtisOrtalama.toFixed(1)}</Typography>
        </Box>
        <Box sx={{ minWidth: 220, p: 3, bgcolor: '#f5f5f5', borderRadius: 2, textAlign: 'center', boxShadow: 1 }}>
          <Typography variant="subtitle2" color="text.secondary">Son Kaç Gün Kaldı</Typography>
          <Typography variant="h5" color="primary" fontWeight={700}>{kalanGun > 0 ? kalanGun : 0}</Typography>
        </Box>
      </Box>
      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 3, mb: 4, justifyContent: 'center', alignItems: 'center' }}>
        <Box sx={{ minWidth: 320, p: 3, bgcolor: '#e8f5e9', borderRadius: 2, textAlign: 'center', boxShadow: 1 }}>
          <Typography variant="subtitle2" color="text.secondary">Son Günün Lideri</Typography>
          <Typography variant="h6" color="primary" fontWeight={700}>{liderAd || '-'}</Typography>
          <Typography variant="body2" color="text.secondary">Artış: {liderArtis > 0 ? `%${liderArtis.toFixed(1)}` : '%0.0'}</Typography>
        </Box>
        <Box sx={{ minWidth: 320, height: 220, p: 3, bgcolor: '#f5f5f5', borderRadius: 2, textAlign: 'center', boxShadow: 1, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
          <Typography variant="subtitle2" color="text.secondary" sx={{ mb: 1 }}>Günlük Artış Dağılımı</Typography>
          <ResponsiveContainer width="100%" height={120}>
            <PieChart>
              <Pie data={pieData} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={40} label>
                {pieData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={pieColors[index % pieColors.length]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </Box>
      </Box>
      <Box sx={{ textAlign: 'center', mt: 4, color: '#888' }}>
        <Typography variant="h6" fontWeight={500}>
          Başarı, küçük adımların toplamıdır. Her gün bir adım daha ileri!
        </Typography>
      </Box>
    </Container>
  );
}

