  };
  return (
    <ThemeProvider theme={theme}>
      <Router>
        <Box sx={{ flexGrow: 1 }}>
          <AppBar position="static">
            <Toolbar>
              <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
                Modern Website
              </Typography>
              <Button color="inherit" component={Link} to="/" startIcon={<HomeIcon />}>Ana Sayfa</Button>
              <Button color="inherit" component={Link} to="/dashboard" startIcon={<BarChartIcon />}>Dashboard</Button>
              <Button color="inherit" component={Link} to="/hedef-nisbet" startIcon={<BarChartIcon />}>Günlük Hedef Nisbeti</Button>
              <Button color="inherit" component={Link} to="/gunluk-artis" startIcon={<BarChartIcon />}>Günlük Artış Yüzdesi</Button>
            </Toolbar>
          </AppBar>
          <Routes>
            <Route path="/" element={<DataTablePage rows={rows} onAddRows={handleAddRows} />} />
            <Route path="/hedef-nisbet" element={<HedefNisbetPage rows={rows} />} />
            <Route path="/gunluk-artis" element={<GunlukArtisYuzdesiPage rows={rows} />} />
            <Route path="/dashboard" element={<DashboardPage rows={rows} />} />
          </Routes>
          {/* Footer */}
          <Box sx={{ 
            bgcolor: 'grey.200', 
            p: 3, 
            textAlign: 'center',
            borderRadius: 2,
            mt: 4
          }}>
            <Typography variant="body2" color="text.secondary">
              © 2024 Modern Website. Tüm hakları saklıdır.
            </Typography>
          </Box>
        </Box>
      </Router>
    </ThemeProvider>
  );
}

export default App;
