import React, { useState } from 'react';
import { 
  AppBar, 
  Toolbar, 
  Typography, 
  Container, 
  Box, 
  Button, 
  Dialog, 
  DialogTitle, 
  DialogContent, 
  DialogActions, 
  TextField, 
  Table, 
  TableBody, 
  TableCell, 
  TableContainer, 
  TableHead, 
  TableRow, 
  Paper, 
  ThemeProvider,
  createTheme
} from '@mui/material';
import { 
  Home as HomeIcon,
  Info as InfoIcon,
  ContactMail as ContactIcon,
  Work as WorkIcon
} from '@mui/icons-material';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

const TABLE_HEADERS = [
  'ID',
  'FAALİYET TİP',
  'Sütun1',
  'AD SOYAD',
  'Sütun2',
  'TİP',
  'TOPLAM',
  'HEDEF',
  'HEDEF NİSBETİ',
];

type TableRowType = string[];

function App() {
  const [rows, setRows] = useState<TableRowType[]>([]);
  const [dialogOpen, setDialogOpen] = useState(false);
  const [inputValue, setInputValue] = useState('');

  const handleOpenDialog = () => setDialogOpen(true);
  const handleCloseDialog = () => {
    setDialogOpen(false);
    setInputValue('');
  };

  // Kopyalanan verileri satır satır ve sütun sütun ayır
  const handleAddRows = () => {
    const newRows = inputValue
      .split('\n')
      .map(line => line.trim())
      .filter(line => line.length > 0)
      .map(line => line.split('\t'));
    setRows(prev => [...prev, ...newRows]);
    handleCloseDialog();
  };

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ flexGrow: 1 }}>
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              Modern Website
            </Typography>
            <Button color="inherit" startIcon={<HomeIcon />}>Ana Sayfa</Button>
            <Button color="inherit" startIcon={<InfoIcon />}>Hakkımızda</Button>
            <Button color="inherit" startIcon={<WorkIcon />}>Hizmetler</Button>
            <Button color="inherit" startIcon={<ContactIcon />}>İletişim</Button>
          </Toolbar>
        </AppBar>

        <Container maxWidth="lg" sx={{ mt: 4 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
            <Typography variant="h5">Veri Tablosu</Typography>
            <Button variant="contained" color="primary" onClick={handleOpenDialog}>
              Ekle
            </Button>
          </Box>

          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  {TABLE_HEADERS.map(header => (
                    <TableCell key={header} sx={{ background: '#6ba6d7', color: 'white', fontWeight: 'bold' }}>{header}</TableCell>
                  ))}
                </TableRow>
              </TableHead>
              <TableBody>
                {rows.map((row, idx) => (
                  <TableRow key={idx}>
                    {TABLE_HEADERS.map((_, colIdx) => (
                      <TableCell key={colIdx}>{row[colIdx] || ''}</TableCell>
                    ))}
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>

          {/* Dialog */}
          <Dialog open={dialogOpen} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
            <DialogTitle>Veri Ekle</DialogTitle>
            <DialogContent>
              <Typography variant="body2" sx={{ mb: 1 }}>
                Lütfen başka bir yerden (ör. Excel) kopyaladığınız verileri buraya yapıştırın. Her satır yeni bir kayıt, sütunlar ise TAB ile ayrılmış olmalı.
              </Typography>
              <TextField
                autoFocus
                multiline
                minRows={6}
                fullWidth
                variant="outlined"
                placeholder={`ID\tFAALİYET TİP\tSütun1\tAD SOYAD\tSütun2\tTİP\tTOPLAM\tHEDEF\tHEDEF NİSBETİ`}
                value={inputValue}
                onChange={e => setInputValue(e.target.value)}
              />
            </DialogContent>
            <DialogActions>
              <Button onClick={handleCloseDialog}>İptal</Button>
              <Button onClick={handleAddRows} variant="contained" color="primary">Ekle</Button>
            </DialogActions>
          </Dialog>

          {/* Footer */}
          <Box sx={{ 
            bgcolor: 'grey.200', 
            p: 3, 
            textAlign: 'center',
            borderRadius: 2,
            mt: 4
          }}>
            <Typography variant="body2" color="text.secondary">
              © 2024 Modern Website. Tüm hakları saklıdır.
            </Typography>
          </Box>
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
