import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { getAuth, onAuthStateChanged, User } from 'firebase/auth';
import { getFirestore, collection, query, where, getDocs } from 'firebase/firestore';
import Navbar from './components/Navbar';
import DashboardPage from './components/DashboardPage';
import DataTablePage from './components/DataTablePage';
import LoginPage from './components/LoginPage';
import { Personel, HedefNisbet, GunlukArtis } from './lib/firebase';
import HomePage from './components/HomePage';

type TableRowType = string[];

function App() {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [rows, setRows] = useState<TableRowType[]>([]);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [user, setUser] = useState<User | null>(null);

  useEffect(() => {
    const auth = getAuth();
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      setUser(user);
      if (!user) {
        setRows([]);
        setLoading(false);
      }
    });

    return () => unsubscribe();
  }, []);

  useEffect(() => {
    async function fetchData() {
      if (!user) {
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        setError(null);

        const db = getFirestore();
        const userUid = user.uid;

        // Personel verilerini çek
        const personelQuery = query(
          collection(db, 'personel'),
          where('userId', '==', userUid)
        );
        const personelSnapshot = await getDocs(personelQuery);
        const personel = personelSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        })) as Personel[];

        // Hedef nisbet verilerini çek
        const hedefQuery = query(
          collection(db, 'hedef_nisbet'),
          where('userId', '==', userUid)
        );
        const hedefSnapshot = await getDocs(hedefQuery);
        const hedefNisbet = hedefSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        })) as HedefNisbet[];

        // Günlük artış verilerini çek
        const artisQuery = query(
          collection(db, 'gunluk_artis'),
          where('userId', '==', userUid)
        );
        const artisSnapshot = await getDocs(artisQuery);
        const gunlukArtis = artisSnapshot.docs.map(doc => ({
          id: doc.id,
          ...doc.data()
        })) as GunlukArtis[];

        // Verileri tablo başlık sırasına göre eşleştir
        const formattedRows = personel.map(p => {
          const hedef = hedefNisbet.find(h => h.personelId === p.id);
          const artis = gunlukArtis.find(g => g.personelId === p.id);

          return [
            p.id || '',
            p.personelNo || '',
            p.departman || '',
            `${p.ad} ${p.soyad}`.trim(),
            p.departman || '',
            artis?.artis.toString() || '0',
            hedef?.hedefDegeri?.toString() || '0',
            `${hedef?.nisbet?.toString() || '0'}%`,
          ];
        });

        setRows(formattedRows);
      } catch (err) {
        console.error('Veri yükleme hatası:', err);
        setError('Veri yüklenirken bir hata oluştu. Lütfen sayfayı yenileyin veya daha sonra tekrar deneyin.');
        setSnackbarOpen(true);
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, [user]);

  const handleCloseSnackbar = () => {
    setSnackbarOpen(false);
  };

  return (
    <Router>
      <div>
        {user && <Navbar />}
        <Routes>
          <Route path="/login" element={!user ? <LoginPage /> : <Navigate to="/dashboard" />} />
          <Route path="/" element={!user ? <Navigate to="/login" /> : <HomePage />} />
          <Route path="/dashboard" element={!user ? <Navigate to="/login" /> : <DashboardPage rows={rows} />} />
          <Route path="/data-table" element={!user ? <Navigate to="/login" /> : <DataTablePage rows={rows} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
