import React, { useState } from 'react';
import { Container, Box, Typography, Button, Dialog, DialogTitle, DialogContent, DialogActions, TextField, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';

const TABLE_HEADERS = [
  'ID',
  'FAALİYET TİP',
  'Sütun1',
  'AD SOYAD',
  'Sütun2',
  'TİP',
  'TOPLAM',
  'HEDEF',
  'HEDEF NİSBETİ',
];

type TableRowType = string[];

function DataTablePage({ rows, onAddRows }: { rows: TableRowType[]; onAddRows: (newRows: TableRowType[]) => void }) {
  const [dialogOpen, setDialogOpen] = useState(false);
  const [inputValue, setInputValue] = useState('');
  const [name, setName] = useState('');
  const [dailyTarget, setDailyTarget] = useState('');
  const [dailyIncrease, setDailyIncrease] = useState('');

  const handleOpenDialog = () => setDialogOpen(true);
  const handleCloseDialog = () => {
    setDialogOpen(false);
    setInputValue('');
  };

  const handleAddRows = () => {
    const newRows = inputValue
      .split('\n')
      .map(line => line.trim())
      .filter(line => line.length > 0)
      .map(line => line.split('\t'));
    onAddRows(newRows);
    handleCloseDialog();
  };

  const handleAddRow = () => {
    if (name && dailyTarget && dailyIncrease) {
      const newRow = [name, dailyTarget, dailyIncrease];
      onAddRows([...rows, newRow]);
      setName('');
      setDailyTarget('');
      setDailyIncrease('');
    }
  };

  const handleDeleteRow = (index: number) => {
    const newRows = rows.filter((_, i) => i !== index);
    onAddRows(newRows);
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Box sx={{ p: { xs: 1, sm: 3 } }}>
        <Typography variant="h4" gutterBottom sx={{ fontSize: { xs: 20, sm: 24, md: 32 }, mb: { xs: 2, sm: 3 } }}>
          Veri Girişi
        </Typography>
        <Box sx={{ display: 'flex', flexDirection: { xs: 'column', sm: 'row' }, gap: 2, mb: 3 }}>
          <TextField
            label="İsim"
            value={name}
            onChange={(e) => setName(e.target.value)}
            fullWidth
            sx={{ mb: { xs: 2, sm: 0 } }}
          />
          <TextField
            label="Günlük Hedef"
            type="number"
            value={dailyTarget}
            onChange={(e) => setDailyTarget(e.target.value)}
            fullWidth
            sx={{ mb: { xs: 2, sm: 0 } }}
          />
          <TextField
            label="Günlük Artış"
            type="number"
            value={dailyIncrease}
            onChange={(e) => setDailyIncrease(e.target.value)}
            fullWidth
            sx={{ mb: { xs: 2, sm: 0 } }}
          />
        </Box>
        <Button variant="contained" onClick={handleAddRow} sx={{ mb: { xs: 2, sm: 3 } }}>
          Satır Ekle
        </Button>
        <TableContainer component={Paper} sx={{ overflowX: 'auto' }}>
          <Table sx={{ minWidth: { xs: 300, sm: 650 } }}>
            <TableHead>
              <TableRow>
                <TableCell sx={{ fontSize: { xs: 12, sm: 14 } }}>İsim</TableCell>
                <TableCell sx={{ fontSize: { xs: 12, sm: 14 } }}>Günlük Hedef</TableCell>
                <TableCell sx={{ fontSize: { xs: 12, sm: 14 } }}>Günlük Artış</TableCell>
                <TableCell sx={{ fontSize: { xs: 12, sm: 14 } }}>İşlemler</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {rows.map((row, index) => (
                <TableRow key={index}>
                  <TableCell sx={{ fontSize: { xs: 12, sm: 14 } }}>{row[0]}</TableCell>
                  <TableCell sx={{ fontSize: { xs: 12, sm: 14 } }}>{row[1]}</TableCell>
                  <TableCell sx={{ fontSize: { xs: 12, sm: 14 } }}>{row[2]}</TableCell>
                  <TableCell>
                    <Button
                      size="small"
                      color="error"
                      onClick={() => handleDeleteRow(index)}
                      sx={{ fontSize: { xs: 11, sm: 13 } }}
                    >
                      Sil
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="h5">Veri Tablosu</Typography>
        <Button variant="contained" color="primary" onClick={handleOpenDialog}>
          Ekle
        </Button>
      </Box>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              {TABLE_HEADERS.map(header => (
                <TableCell key={header} sx={{ background: '#6ba6d7', color: 'white', fontWeight: 'bold' }}>{header}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row, idx) => (
              <TableRow key={idx}>
                {TABLE_HEADERS.map((_, colIdx) => (
                  <TableCell key={colIdx}>{row[colIdx] || ''}</TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      {/* Dialog */}
      <Dialog open={dialogOpen} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
        <DialogTitle>Veri Ekle</DialogTitle>
        <DialogContent>
          <Typography variant="body2" sx={{ mb: 1 }}>
            Lütfen başka bir yerden (ör. Excel) kopyaladığınız verileri buraya yapıştırın. Her satır yeni bir kayıt, sütunlar ise TAB ile ayrılmış olmalı.
          </Typography>
          <TextField
            autoFocus
            multiline
            minRows={6}
            fullWidth
            variant="outlined"
            placeholder={`ID\tFAALİYET TİP\tSütun1\tAD SOYAD\tSütun2\tTİP\tTOPLAM\tHEDEF\tHEDEF NİSBETİ`}
            value={inputValue}
            onChange={e => setInputValue(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>İptal</Button>
          <Button onClick={handleAddRows} variant="contained" color="primary">Ekle</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default DataTablePage;
