import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { getAuth, onAuthStateChanged } from 'firebase/auth';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import { Box, CircularProgress } from '@mui/material';
import LoginPage from './components/LoginPage';
import DashboardPage from './components/DashboardPage';
import HedefNisbetPage from './components/HedefNisbetPage';
import GunlukArtisYuzdesiPage from './components/GunlukArtisYuzdesiPage';
import Navbar from './components/Navbar';
import { getPersonelList, getHedefNisbetList, getGunlukArtisList } from './lib/firebase';

const theme = createTheme({
  palette: {
    mode: 'light',
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
  typography: {
    fontFamily: '"Roboto", "Helvetica", "Arial", sans-serif',
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none',
        },
      },
    },
  },
});

function App() {
  const [user, setUser] = useState<any>(null);
  const [loading, setLoading] = useState(true);
  const [rows, setRows] = useState<any[]>([]);
  const auth = getAuth();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (user) => {
      setUser(user);
      if (user) {
        try {
          // Kullanıcıya özel verileri getir
          const [personel, hedefNisbet, gunlukArtis] = await Promise.all([
            getPersonelList(user.uid),
            getHedefNisbetList(user.uid),
            getGunlukArtisList(user.uid)
          ]);

          // Verileri tablo başlık sırasına göre eşleştir
          const formattedRows = personel.map(p => {
            const hedef = hedefNisbet.find(h => h.personelId === p.id);
            const artis = gunlukArtis.find(g => g.personelId === p.id);

            return [
              p.id || '', // Gizli ID sütunu
              p.personelNo || '', // PersonelNO
              p.departman || '', // FAALİYET TİP
              `${p.ad} ${p.soyad}`.trim(), // AD SOYAD
              p.departman || '', // TİP
              artis?.artis.toString() || '0', // TOPLAM
              hedef?.hedefDegeri?.toString() || '0', // HEDEF
              `${hedef?.nisbet?.toString() || '0'}%`, // HEDEF NİSBETİ
              hedef?.tarih || artis?.tarih || '' // Tarih
            ];
          });

          setRows(formattedRows);
        } catch (error) {
          console.error('Veri yükleme hatası:', error);
        }
      }
      setLoading(false);
    });

    return () => unsubscribe();
  }, [auth]);

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <Routes>
          <Route path="/login" element={!user ? <LoginPage /> : <Navigate to="/dashboard" />} />
          <Route
            path="/"
            element={
              user ? (
                <>
                  <Navbar />
                  <DashboardPage rows={rows} />
                </>
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route
            path="/dashboard"
            element={
              user ? (
                <>
                  <Navbar />
                  <DashboardPage rows={rows} />
                </>
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route
            path="/hedef-nisbet"
            element={
              user ? (
                <>
                  <Navbar />
                  <HedefNisbetPage rows={rows} />
                </>
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route
            path="/gunluk-artis"
            element={
              user ? (
                <>
                  <Navbar />
                  <GunlukArtisYuzdesiPage rows={rows} />
                </>
              ) : (
                <Navigate to="/login" />
              )
            }
          />
        </Routes>
      </Router>
    </ThemeProvider>
  );
}

export default App;
