import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, Box, Button, ThemeProvider, createTheme, Container, CircularProgress, Alert, Snackbar } from '@mui/material';
import { Home as HomeIcon, BarChart as BarChartIcon } from '@mui/icons-material';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import DataTablePage from './components/DataTablePage';
import HedefNisbetPage from './components/HedefNisbetPage';
import GunlukArtisYuzdesiPage from './components/GunlukArtisYuzdesiPage';
import DashboardPage from './components/DashboardPage';
import { getPersonelList, getHedefNisbetList, getGunlukArtisList, Personel, HedefNisbet, GunlukArtis } from './lib/firebase';
import { CssBaseline } from '@mui/material';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

type TableRowType = string[];

function App() {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [rows, setRows] = useState<TableRowType[]>([]);
  const [snackbarOpen, setSnackbarOpen] = useState(false);

  useEffect(() => {
    async function fetchData() {
      try {
        setLoading(true);
        setError(null);

        const [personel, hedefNisbet, gunlukArtis] = await Promise.all([
          getPersonelList(),
          getHedefNisbetList(),
          getGunlukArtisList()
        ]);

        // Verileri tablo başlık sırasına göre eşleştir
        const formattedRows = personel.map(p => {
          const hedef = hedefNisbet.find(h => h.personelId === p.id);
          const artis = gunlukArtis.find(g => g.personelId === p.id);

          return [
            p.id || '', // Gizli ID sütunu (seçme/silme için gerekli, kullanıcıya gösterilmez) -> row[0]
            p.personelNo || '', // PersonelNO -> row[1]
            p.departman || '', // FAALİYET TİP (şimdilik departman kullanılıyor) -> row[2]
            `${p.ad} ${p.soyad}`.trim(), // AD SOYAD -> row[3]
            p.departman || '', // TİP (şimdilik departman kullanılıyor) -> row[4]
            artis?.artis.toString() || '0', // TOPLAM (Günlük artış) -> row[5]
            hedef?.hedefDegeri?.toString() || '0', // HEDEF (Yeni eklenen hedefDegeri kullanılıyor) -> row[6]
            `${hedef?.nisbet?.toString() || '0'}%`, // HEDEF NİSBETİ -> row[7]
            hedef?.tarih || artis?.tarih || '' // Tarih -> row[8] (Hedef veya Günlük Artış kaydından alınır)
          ];
        });

        setRows(formattedRows);
      } catch (err) {
        console.error('Veri yükleme hatası:', err);
        setError('Veri yüklenirken bir hata oluştu. Lütfen sayfayı yenileyin veya daha sonra tekrar deneyin.');
        setSnackbarOpen(true);
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, []);

  const handleCloseSnackbar = () => {
    setSnackbarOpen(false);
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <CircularProgress />
      </Box>
    );
  }

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
          <AppBar position="static">
            <Toolbar sx={{ flexDirection: { xs: 'column', sm: 'row' }, alignItems: { xs: 'flex-start', sm: 'center' }, gap: { xs: 1, sm: 2 }, px: { xs: 1, sm: 2 } }}>
              <Typography variant="h6" component="div" sx={{ flexGrow: 1, fontSize: { xs: 18, sm: 24 }, mb: { xs: 1, sm: 0 } }}>
                Mavera Dijital
              </Typography>
              <Box sx={{ display: 'flex', flexDirection: { xs: 'column', sm: 'row' }, width: { xs: '100%', sm: 'auto' }, gap: 1 }}>
                <Button color="inherit" component={Link} to="/" startIcon={<HomeIcon />} fullWidth={true} sx={{ minWidth: 120 }}>{'Ana Sayfa'}</Button>
                <Button color="inherit" component={Link} to="/dashboard" startIcon={<BarChartIcon />} fullWidth={true} sx={{ minWidth: 120 }}>{'Dashboard'}</Button>
                <Button color="inherit" component={Link} to="/hedef-nisbet" startIcon={<BarChartIcon />} fullWidth={true} sx={{ minWidth: 120 }}>{'Günlük Hedef Nisbeti'}</Button>
                <Button color="inherit" component={Link} to="/gunluk-artis" startIcon={<BarChartIcon />} fullWidth={true} sx={{ minWidth: 120 }}>{'Günlük Artış Yüzdesi'}</Button>
              </Box>
            </Toolbar>
          </AppBar>
          <Box component="main" sx={{ flexGrow: 1, bgcolor: 'background.default' }}>
            <Routes>
              <Route path="/" element={<DataTablePage rows={rows} />} />
              <Route path="/dashboard" element={<DashboardPage rows={rows} />} />
              <Route path="/hedef-nisbet" element={<HedefNisbetPage rows={rows} />} />
              <Route path="/gunluk-artis" element={<GunlukArtisYuzdesiPage rows={rows} />} />
            </Routes>
          </Box>
        </Box>
      </Router>
      <Snackbar open={snackbarOpen} autoHideDuration={6000} onClose={handleCloseSnackbar}>
        <Alert onClose={handleCloseSnackbar} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>
    </ThemeProvider>
  );
}

export default App;
