import React, { useState } from 'react';
import { Container, Typography, Box, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Button, Dialog, DialogTitle, DialogContent, DialogActions, TextField, Alert, Snackbar } from '@mui/material';
import { createPersonel, createHedefNisbet, createGunlukArtis } from '../lib/firebase';

type TableRowType = string[];

function DataTablePage({ rows }: { rows: TableRowType[] }) {
  const [open, setOpen] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [newRow, setNewRow] = useState({
    ad: '',
    soyad: '',
    departman: '',
    nisbet: '',
    artis: '',
    tarih: new Date().toISOString().split('T')[0]
  });

  const handleAddRow = async () => {
    try {
      setLoading(true);
      setError(null);

      // Önce personel oluştur
      const personel = await createPersonel({
        ad: newRow.ad,
        soyad: newRow.soyad,
        departman: newRow.departman
      });

      // Sonra hedef nisbet oluştur
      await createHedefNisbet({
        personelId: personel.id!,
        nisbet: parseFloat(newRow.nisbet),
        tarih: newRow.tarih
      });

      // Son olarak günlük artış oluştur
      await createGunlukArtis({
        personelId: personel.id!,
        artis: parseFloat(newRow.artis),
        tarih: newRow.tarih
      });

      setOpen(false);
      setNewRow({
        ad: '',
        soyad: '',
        departman: '',
        nisbet: '',
        artis: '',
        tarih: new Date().toISOString().split('T')[0]
      });

      // Sayfayı yenile
      window.location.reload();
    } catch (error) {
      console.error('Veri eklenirken hata oluştu:', error);
      setError('Veri eklenirken bir hata oluştu. Lütfen tekrar deneyin.');
      setSnackbarOpen(true);
    } finally {
      setLoading(false);
    }
  };

  const handleCloseSnackbar = () => {
    setSnackbarOpen(false);
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Box sx={{ p: { xs: 1, sm: 3 } }}>
        <Typography variant="h4" gutterBottom sx={{ fontSize: { xs: 20, sm: 24, md: 32 }, mb: { xs: 2, sm: 3 } }}>
          Veri Tablosu
        </Typography>
        <Box sx={{ mb: 3 }}>
          <Button 
            variant="contained" 
            color="primary" 
            onClick={() => setOpen(true)}
            disabled={loading}
          >
            {loading ? 'Ekleniyor...' : 'Yeni Veri Ekle'}
          </Button>
        </Box>
        <TableContainer component={Paper} sx={{ overflowX: 'auto' }}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>ID</TableCell>
                <TableCell>Ad</TableCell>
                <TableCell>Soyad</TableCell>
                <TableCell>Departman</TableCell>
                <TableCell>Hedef Nisbet</TableCell>
                <TableCell>Günlük Artış</TableCell>
                <TableCell>Tarih</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {rows.map((row, index) => (
                <TableRow key={index}>
                  {row.map((cell, cellIndex) => (
                    <TableCell key={cellIndex}>{cell}</TableCell>
                  ))}
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Box>

      <Dialog open={open} onClose={() => !loading && setOpen(false)}>
        <DialogTitle>Yeni Veri Ekle</DialogTitle>
        <DialogContent>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, pt: 2 }}>
            <TextField
              label="Ad"
              value={newRow.ad}
              onChange={(e) => setNewRow({ ...newRow, ad: e.target.value })}
              fullWidth
              disabled={loading}
            />
            <TextField
              label="Soyad"
              value={newRow.soyad}
              onChange={(e) => setNewRow({ ...newRow, soyad: e.target.value })}
              fullWidth
              disabled={loading}
            />
            <TextField
              label="Departman"
              value={newRow.departman}
              onChange={(e) => setNewRow({ ...newRow, departman: e.target.value })}
              fullWidth
              disabled={loading}
            />
            <TextField
              label="Hedef Nisbet"
              type="number"
              value={newRow.nisbet}
              onChange={(e) => setNewRow({ ...newRow, nisbet: e.target.value })}
              fullWidth
              disabled={loading}
            />
            <TextField
              label="Günlük Artış"
              type="number"
              value={newRow.artis}
              onChange={(e) => setNewRow({ ...newRow, artis: e.target.value })}
              fullWidth
              disabled={loading}
            />
            <TextField
              label="Tarih"
              type="date"
              value={newRow.tarih}
              onChange={(e) => setNewRow({ ...newRow, tarih: e.target.value })}
              fullWidth
              disabled={loading}
              InputLabelProps={{ shrink: true }}
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpen(false)} disabled={loading}>İptal</Button>
          <Button onClick={handleAddRow} variant="contained" color="primary" disabled={loading}>
            {loading ? 'Ekleniyor...' : 'Ekle'}
          </Button>
        </DialogActions>
      </Dialog>

      <Snackbar open={snackbarOpen} autoHideDuration={6000} onClose={handleCloseSnackbar}>
        <Alert onClose={handleCloseSnackbar} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>
    </Container>
  );
}

export default DataTablePage;
