import React from 'react';
import { Container, Typography, Box, Card, CardContent, TableContainer, Paper, Table, TableHead, TableRow, TableCell, TableBody, Grid } from '@mui/material';
import ArrowUpwardIcon from '@mui/icons-material/ArrowUpward';
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';
import { BarChart, CartesianGrid, XAxis, YAxis, Tooltip, Legend, Bar } from 'recharts';
import { ResponsiveContainer } from 'recharts';

type TableRowType = string[];

function GunlukArtisYuzdesiPage({ rows }: { rows: TableRowType[] }) {
  const idAdList: { id: string, ad: string }[] = [];
  const idSet = new Set<string>();
  rows.forEach(row => {
    if (row[0] && !idSet.has(row[0])) {
      idAdList.push({ id: row[0], ad: row[3] });
      idSet.add(row[0]);
    }
  });
  const kisiSayisi = idAdList.length;
  const toplamGun = Math.floor(rows.length / kisiSayisi);
  const idyeGoreNisbet: Record<string, string[]> = {};
  idAdList.forEach(({ id }) => {
    idyeGoreNisbet[id] = [];
  });
  for (let gun = 0; gun < toplamGun; gun++) {
    for (let kisi = 0; kisi < kisiSayisi; kisi++) {
      const rowIdx = gun * kisiSayisi + kisi;
      const row = rows[rowIdx];
      if (row && row[0] && idyeGoreNisbet[row[0]]) {
        idyeGoreNisbet[row[0]][gun] = row[8];
      }
    }
  }
  const columns = ['ID', 'AD SOYAD', 'Son Gün', 'Bir Önceki Gün', 'Artış (%)'];
  function parsePercent(val: string | undefined) {
    if (!val) return 0;
    return parseFloat(val.replace('%', '').replace(',', '.')) || 0;
  }
  const sonGunNisbetleri = idAdList.map(({ id }) => {
    const sonGun = toplamGun > 0 ? idyeGoreNisbet[id][toplamGun - 1] : '';
    return parsePercent(sonGun);
  });
  const ortalama = sonGunNisbetleri.length > 0
    ? sonGunNisbetleri.reduce((a, b) => a + b, 0) / sonGunNisbetleri.length
    : 0;
  let liderAd = '';
  let liderArtis = -Infinity;
  idAdList.forEach(({ id, ad }) => {
    const sonGun = toplamGun > 0 ? idyeGoreNisbet[id][toplamGun - 1] : '';
    const oncekiGun = toplamGun > 1 ? idyeGoreNisbet[id][toplamGun - 2] : '';
    const artis = parsePercent(sonGun) - parsePercent(oncekiGun);
    if (artis > liderArtis) {
      liderArtis = artis;
      liderAd = ad;
    }
  });
  const artisliListe = idAdList.map(({ id, ad }) => {
    const sonGun = toplamGun > 0 ? idyeGoreNisbet[id][toplamGun - 1] : '';
    const oncekiGun = toplamGun > 1 ? idyeGoreNisbet[id][toplamGun - 2] : '';
    const artis = parsePercent(sonGun) - parsePercent(oncekiGun);
    const sonGunYuzde = parsePercent(sonGun);
    return {
      id,
      ad,
      sonGun,
      oncekiGun,
      artis,
      sonGunYuzde
    };
  });
  artisliListe.sort((a, b) => b.artis - a.artis);
  const gunlukArtisOrtalama = artisliListe.length > 0
    ? artisliListe.reduce((a, b) => a + b.artis, 0) / artisliListe.length
    : 0;
  const hedefTarih = new Date('2025-06-07');
  const bugun = new Date();
  const kalanGun = Math.ceil((hedefTarih.getTime() - bugun.getTime()) / (1000 * 60 * 60 * 24));
  const infoBoxes = [
    {
      label: 'Genel Günlük Artış Ortalaması',
      value: `%${gunlukArtisOrtalama.toFixed(1)}`
    },
    {
      label: 'Hedefe Olan Nisbet Oranı',
      value: `%${ortalama.toFixed(1)}`
    },
    {
      label: 'Son Kaç Gün Kaldı',
      value: kalanGun > 0 ? kalanGun : 0
    }
  ];
  const chartData = [
    { name: 'Genel', artis: gunlukArtisOrtalama },
    { name: 'Hedefe Olan', artis: ortalama },
    { name: 'Son Kaç Gün Kaldı', artis: kalanGun }
  ];
  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <img
        src="/motivasyon.png"
        alt="Motivasyon"
        style={{
          width: '100%',
          maxWidth: 600,
          display: 'block',
          margin: '24px auto 16px auto',
          borderRadius: 16,
          boxShadow: '0 2px 8px rgba(0,0,0,0.08)'
        }}
      />
      <Box sx={{ p: { xs: 1, sm: 3 } }}>
        <Typography variant="h4" gutterBottom sx={{ fontSize: { xs: 20, sm: 24, md: 32 }, mb: { xs: 2, sm: 3 } }}>
          Günlük Artış Yüzdesi
        </Typography>
        <Box sx={{ display: 'grid', gridTemplateColumns: { xs: '1fr', md: '1fr 1fr' }, gap: { xs: 2, sm: 3 } }}>
          <Box>
            <Card sx={{ height: '100%', minHeight: { xs: 300, sm: 400 } }}>
              <CardContent>
                <Typography variant="h6" sx={{ fontSize: { xs: 16, sm: 20 }, mb: 2 }}>
                  Günlük Artış Yüzdesi Grafiği
                </Typography>
                <ResponsiveContainer width="100%" height={300}>
                  <BarChart data={chartData}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="name" tick={{ fontSize: 12 }} />
                    <YAxis tick={{ fontSize: 12 }} />
                    <Tooltip />
                    <Legend />
                    <Bar dataKey="artis" fill="#82ca9d" />
                  </BarChart>
                </ResponsiveContainer>
              </CardContent>
            </Card>
          </Box>
          <Box>
            <Card sx={{ height: '100%' }}>
              <CardContent>
                <Typography variant="h6" sx={{ fontSize: { xs: 16, sm: 20 }, mb: 2 }}>
                  Detaylı Liste
                </Typography>
                <TableContainer component={Paper} sx={{ overflowX: 'auto' }}>
                  <Table size="small">
                    <TableHead>
                      <TableRow>
                        <TableCell sx={{ fontSize: { xs: 12, sm: 14 } }}>İsim</TableCell>
                        <TableCell sx={{ fontSize: { xs: 12, sm: 14 } }}>Artış (%)</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {chartData.map((row, index) => (
                        <TableRow key={index}>
                          <TableCell sx={{ fontSize: { xs: 12, sm: 14 } }}>{row.name}</TableCell>
                          <TableCell sx={{ fontSize: { xs: 12, sm: 14 } }}>{row.artis}%</TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>
              </CardContent>
            </Card>
          </Box>
        </Box>
      </Box>
      {toplamGun > 1 && liderAd && (
        <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
          <Box sx={{ flex: 2, p: 2, bgcolor: '#e3f2fd', borderRadius: 2, textAlign: 'center' }}>
            <Typography variant="h6" color="primary">
              Günün Lideri: {liderAd} ({liderArtis > 0 ? `%${liderArtis.toFixed(1)}` : '%0.0'})
            </Typography>
          </Box>
          {infoBoxes.map((box, i) => (
            <Card key={i} sx={{ flex: 1, minWidth: 120, borderRadius: 2, textAlign: 'center', border: '1px solid #90caf9', boxShadow: 2 }}>
              <CardContent>
                <Typography variant="subtitle2" color="text.secondary">{box.label}</Typography>
                <Typography variant="h6" color="primary">{box.value}</Typography>
              </CardContent>
            </Card>
          ))}
        </Box>
      )}
      <Typography variant="body2" sx={{ mb: 2 }}>
        Son gün HEDEF NİSBETİ ortalaması: <b>%{ortalama.toFixed(1)}</b><br/>
        Günlük artış ortalaması: <b>%{gunlukArtisOrtalama.toFixed(1)}</b>
      </Typography>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              {columns.map(col => (
                <TableCell key={col} sx={{ background: '#6ba6d7', color: 'white', fontWeight: 'bold' }}>{col}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {artisliListe.map(({ id, ad, sonGun, oncekiGun, artis, sonGunYuzde }) => {
              let bgColor = '#fffde7';
              if (artis > gunlukArtisOrtalama) bgColor = '#e8f5e9';
              else if (artis < gunlukArtisOrtalama) bgColor = '#ffebee';
              return (
                <TableRow
                  key={id}
                  sx={{ backgroundColor: bgColor }}
                >
                  <TableCell>{id}</TableCell>
                  <TableCell>{ad}</TableCell>
                  <TableCell>{sonGun || ''}</TableCell>
                  <TableCell>{oncekiGun || ''}</TableCell>
                  <TableCell>{toplamGun > 1 ? `%${artis.toFixed(1)}` : ''}</TableCell>
                </TableRow>
              );
            })}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
}

export default GunlukArtisYuzdesiPage;
