import React, { useState } from 'react';
import { AppBar, Toolbar, Typography, Box, Button, ThemeProvider, createTheme, Container } from '@mui/material';
import { Home as HomeIcon, BarChart as BarChartIcon } from '@mui/icons-material';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import DataTablePage from './components/DataTablePage';
import HedefNisbetPage from './components/HedefNisbetPage';
import GunlukArtisYuzdesiPage from './components/GunlukArtisYuzdesiPage';
import DashboardPage from './components/DashboardPage';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

type TableRowType = string[];

function App() {
  const [rows, setRows] = useState<TableRowType[]>([]);
  const handleAddRows = (newRows: TableRowType[]) => {
    setRows(prev => [...prev, ...newRows]);
  };
  return (
    <ThemeProvider theme={theme}>
      <Router>
        <Box sx={{ flexGrow: 1 }}>
          <AppBar position="static">
            <Toolbar>
              <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
                Mavera Dijital
              </Typography>
              <Button color="inherit" component={Link} to="/" startIcon={<HomeIcon />}>Ana Sayfa</Button>
              <Button color="inherit" component={Link} to="/dashboard" startIcon={<BarChartIcon />}>Dashboard</Button>
              <Button color="inherit" component={Link} to="/hedef-nisbet" startIcon={<BarChartIcon />}>Günlük Hedef Nisbeti</Button>
              <Button color="inherit" component={Link} to="/gunluk-artis" startIcon={<BarChartIcon />}>Günlük Artış Yüzdesi</Button>
            </Toolbar>
          </AppBar>
          <Routes>
            <Route path="/" element={<DataTablePage rows={rows} onAddRows={handleAddRows} />} />
            <Route path="/hedef-nisbet" element={<HedefNisbetPage rows={rows} />} />
            <Route path="/gunluk-artis" element={<GunlukArtisYuzdesiPage rows={rows} />} />
            <Route path="/dashboard" element={<DashboardPage rows={rows} />} />
          </Routes>
          {/* Footer */}
          <Container maxWidth="lg">
            <Box sx={{ bgcolor: 'grey.200', p: 3, textAlign: 'center', borderRadius: 2, mt: 4 }}>
              <Typography variant="body2" color="text.secondary">
                © 2025 Powered By Mavera. Tüm hakları saklıdır.
              </Typography>
            </Box>
          </Container>
        </Box>
      </Router>
    </ThemeProvider>
  );
}

export default App;
