import React, { useState } from 'react';
import { 
  AppBar, 
  Toolbar, 
  Typography, 
  Container, 
  Box, 
  Button, 
  Dialog, 
  DialogTitle, 
  DialogContent, 
  DialogActions, 
  TextField, 
  Table, 
  TableBody, 
  TableCell, 
  TableContainer, 
  TableHead, 
  TableRow, 
  Paper, 
  ThemeProvider,
  createTheme
} from '@mui/material';
import { 
  Home as HomeIcon,
  Info as InfoIcon,
  ContactMail as ContactIcon,
  Work as WorkIcon,
  BarChart as BarChartIcon
} from '@mui/icons-material';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
  },
});

const TABLE_HEADERS = [
  'ID',
  'FAALİYET TİP',
  'Sütun1',
  'AD SOYAD',
  'Sütun2',
  'TİP',
  'TOPLAM',
  'HEDEF',
  'HEDEF NİSBETİ',
];

type TableRowType = string[];

function DataTablePage({ rows, onAddRows }: { rows: TableRowType[]; onAddRows: (newRows: TableRowType[]) => void }) {
  const [dialogOpen, setDialogOpen] = useState(false);
  const [inputValue, setInputValue] = useState('');

  const handleOpenDialog = () => setDialogOpen(true);
  const handleCloseDialog = () => {
    setDialogOpen(false);
    setInputValue('');
  };

  const handleAddRows = () => {
    const newRows = inputValue
      .split('\n')
      .map(line => line.trim())
      .filter(line => line.length > 0)
      .map(line => line.split('\t'));
    onAddRows(newRows);
    handleCloseDialog();
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="h5">Veri Tablosu</Typography>
        <Button variant="contained" color="primary" onClick={handleOpenDialog}>
          Ekle
        </Button>
      </Box>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              {TABLE_HEADERS.map(header => (
                <TableCell key={header} sx={{ background: '#6ba6d7', color: 'white', fontWeight: 'bold' }}>{header}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {rows.map((row, idx) => (
              <TableRow key={idx}>
                {TABLE_HEADERS.map((_, colIdx) => (
                  <TableCell key={colIdx}>{row[colIdx] || ''}</TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      {/* Dialog */}
      <Dialog open={dialogOpen} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
        <DialogTitle>Veri Ekle</DialogTitle>
        <DialogContent>
          <Typography variant="body2" sx={{ mb: 1 }}>
            Lütfen başka bir yerden (ör. Excel) kopyaladığınız verileri buraya yapıştırın. Her satır yeni bir kayıt, sütunlar ise TAB ile ayrılmış olmalı.
          </Typography>
          <TextField
            autoFocus
            multiline
            minRows={6}
            fullWidth
            variant="outlined"
            placeholder={`ID\tFAALİYET TİP\tSütun1\tAD SOYAD\tSütun2\tTİP\tTOPLAM\tHEDEF\tHEDEF NİSBETİ`}
            value={inputValue}
            onChange={e => setInputValue(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>İptal</Button>
          <Button onClick={handleAddRows} variant="contained" color="primary">Ekle</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

function HedefNisbetPage({ rows }: { rows: TableRowType[] }) {
  const today = new Date().toLocaleDateString('tr-TR');

  // Her gün kaç satır var? (ilk günün ID sayısı kadar)
  // En çok tekrar eden ID sayısını gün uzunluğu olarak alıyoruz.
  const idCounts: Record<string, number> = {};
  rows.forEach(row => {
    if (row[0]) idCounts[row[0]] = (idCounts[row[0]] || 0) + 1;
  });
  const gunKisiSayisi = Math.max(...Object.values(idCounts));
  const toplamGun = Math.ceil(rows.length / gunKisiSayisi);

  // ID ve ad-soyad listesini ilk günün verisinden al
  const idAdList: { id: string, ad: string }[] = [];
  for (let i = 0; i < gunKisiSayisi; i++) {
    if (rows[i]) idAdList.push({ id: rows[i][0], ad: rows[i][3] });
  }

  // ID'ye göre her günün HEDEF NİSBETİ değerlerini topla
  const idyeGoreNisbet: Record<string, string[]> = {};
  idAdList.forEach(({ id }) => {
    idyeGoreNisbet[id] = [];
  });
  for (let gun = 0; gun < toplamGun; gun++) {
    for (let kisi = 0; kisi < gunKisiSayisi; kisi++) {
      const rowIdx = gun * gunKisiSayisi + kisi;
      const row = rows[rowIdx];
      if (row && row[0] && idyeGoreNisbet[row[0]]) {
        idyeGoreNisbet[row[0]][gun] = row[8];
      }
    }
  }

  // Sütun başlıkları: ID, AD SOYAD, 1. Gün, 2. Gün, ...
  const columns = ['ID', 'AD SOYAD', ...Array.from({ length: toplamGun }, (_, i) => `${i + 1}. Gün`)];
  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Typography variant="h5" sx={{ mb: 2 }}>Günlük Hedef Nisbeti ({today})</Typography>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              {columns.map(col => (
                <TableCell key={col} sx={{ background: '#6ba6d7', color: 'white', fontWeight: 'bold' }}>{col}</TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {idAdList.map(({ id, ad }) => (
              <TableRow key={id}>
                <TableCell>{id}</TableCell>
                <TableCell>{ad}</TableCell>
                {Array.from({ length: toplamGun }).map((_, gunIdx) => (
                  <TableCell key={gunIdx}>{idyeGoreNisbet[id][gunIdx] || ''}</TableCell>
                ))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
}

function App() {
  const [rows, setRows] = useState<TableRowType[]>([]);
  const handleAddRows = (newRows: TableRowType[]) => {
    setRows(prev => [...prev, ...newRows]);
  };
  return (
    <ThemeProvider theme={theme}>
      <Router>
        <Box sx={{ flexGrow: 1 }}>
          <AppBar position="static">
            <Toolbar>
              <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
                Modern Website
              </Typography>
              <Button color="inherit" component={Link} to="/" startIcon={<HomeIcon />}>Ana Sayfa</Button>
              <Button color="inherit" component={Link} to="/hedef-nisbet" startIcon={<BarChartIcon />}>Günlük Hedef Nisbeti</Button>
            </Toolbar>
          </AppBar>
          <Routes>
            <Route path="/" element={<DataTablePage rows={rows} onAddRows={handleAddRows} />} />
            <Route path="/hedef-nisbet" element={<HedefNisbetPage rows={rows} />} />
          </Routes>
          {/* Footer */}
          <Box sx={{ 
            bgcolor: 'grey.200', 
            p: 3, 
            textAlign: 'center',
            borderRadius: 2,
            mt: 4
          }}>
            <Typography variant="body2" color="text.secondary">
              © 2024 Modern Website. Tüm hakları saklıdır.
            </Typography>
          </Box>
        </Box>
      </Router>
    </ThemeProvider>
  );
}

export default App;
