import React from 'react';
import { AppBar, Toolbar, Typography, Box, Button, IconButton } from '@mui/material';
import { Home as HomeIcon, BarChart as BarChartIcon, Logout as LogoutIcon } from '@mui/icons-material';
import { Link, useNavigate } from 'react-router-dom';
import { getAuth, signOut } from 'firebase/auth';

function Navbar() {
  const navigate = useNavigate();
  const auth = getAuth();

  const handleLogout = async () => {
    try {
      await signOut(auth);
      navigate('/login');
    } catch (error) {
      console.error('Çıkış yapılırken hata oluştu:', error);
    }
  };

  return (
    <AppBar position="static">
      <Toolbar sx={{ 
        flexDirection: { xs: 'column', sm: 'row' }, 
        alignItems: { xs: 'flex-start', sm: 'center' }, 
        gap: { xs: 1, sm: 2 }, 
        px: { xs: 1, sm: 2 } 
      }}>
        <Typography 
          variant="h6" 
          component="div" 
          sx={{ 
            flexGrow: 1, 
            fontSize: { xs: 18, sm: 24 }, 
            mb: { xs: 1, sm: 0 } 
          }}
        >
          Mavera Dijital
        </Typography>
        <Box sx={{ 
          display: 'flex', 
          flexDirection: { xs: 'column', sm: 'row' }, 
          width: { xs: '100%', sm: 'auto' }, 
          gap: 1 
        }}>
          <Button 
            color="inherit" 
            component={Link} 
            to="/" 
            startIcon={<HomeIcon />} 
            fullWidth={true} 
            sx={{ minWidth: 120 }}
          >
            Ana Sayfa
          </Button>
          <Button 
            color="inherit" 
            component={Link} 
            to="/dashboard" 
            startIcon={<BarChartIcon />} 
            fullWidth={true} 
            sx={{ minWidth: 120 }}
          >
            Dashboard
          </Button>
          <Button 
            color="inherit" 
            component={Link} 
            to="/hedef-nisbet" 
            startIcon={<BarChartIcon />} 
            fullWidth={true} 
            sx={{ minWidth: 120 }}
          >
            Günlük Hedef Nisbeti
          </Button>
          <Button 
            color="inherit" 
            component={Link} 
            to="/gunluk-artis" 
            startIcon={<BarChartIcon />} 
            fullWidth={true} 
            sx={{ minWidth: 120 }}
          >
            Günlük Artış Yüzdesi
          </Button>
          <IconButton 
            color="inherit" 
            onClick={handleLogout}
            sx={{ 
              ml: { sm: 1 },
              alignSelf: { xs: 'flex-end', sm: 'center' }
            }}
          >
            <LogoutIcon />
          </IconButton>
        </Box>
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
